import { useState } from "react";
import useInput from "../hooks/use-input";

const isNotEmpty = (value) => value.trim() !== "";
const isDescription = (value) => value.length < 100;

const UpdatePost = (props) => {

    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredTitleIsValid, setEnteredTitleIsValid] = useState(true);

    const [enteredContent, setEnteredContent] = useState('');
    const [enteredContentIsValid, setEnteredContentIsValid] = useState(true);

    const [enteredDescription, setEnteredDescription] = useState('');
    const [enteredDescriptionIsValid, setEnteredDescriptionIsValid] = useState(true);

    setEnteredTitle(props.post.title);
    setEnteredContent(props.post.content);
    setEnteredDescription(props.post.décription);

    const titleInputChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    }
    const contentInputChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    }
    const descriptionInputChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    }

    const submitHandler = (event) => {
        event.preventDefault();

        if (enteredTitleIsValid.trim() === '') {
            setEnteredTitleIsValid(false);
            return;
        }
        
        if (enteredContentIsValid.trim() === '') {
            setEnteredTitleIsValid(false);
            return;
        }
        
        if (enteredDescriptionIsValid.trim() === '') {
            setEnteredTitleIsValid(false);
            return;
        }
        setEnteredTitleIsValid(true);
        setEnteredContentIsValid(true);
        setEnteredDescriptionIsValid(true);

        const post = {
            id: props.post.id,
            content: props.post.content,
            description: props.post.description,
            title: props.post.title,
            categoryId: 2
        };

        props.onUpdatePost(post);

    };

    return (
        <form onSubmit={submitHandler}>
            <div className="control-group">
                <div className="">
                    <label htmlFor="name">Title</label>
                    <input
                        type="text"
                        id="name"
                        value={enteredTitle}
                        onChange={titleInputChangeHandler}
                    />
                    {!enteredTitleIsValid &&
                        <p className="error-text">Please enter a title.</p>
                    }

                </div>
                <div className="">
                    <label htmlFor="name">Content</label>
                    <input
                        type="text"
                        id="name"
                        value={enteredContent}
                        onChange={contentInputChangeHandler}
                    />
                     {!enteredTitleIsValid &&
                        <p className="error-text">Please enter a title.</p>
                    }

                </div>
            </div>
            <div className="">
                <label htmlFor="name">Description</label>
                <input
                    type="text"
                    id="name"
                    value={enteredDescription}
                    onChange={descriptionInputChangeHandler}

                />
                 {!enteredTitleIsValid &&
                        <p className="error-text">Please enter a title.</p>
                    }

            </div>
            <div className="form-actions">
                <button >Submit</button>
                <button className="button-cancel" onClick={() => {
                    props.onChangePage(false);
                }}>Cancel</button>
            </div>
        </form>
    );
};

export default UpdatePost;
