import React, { useState, useEffect, useCallback } from "react";

import PostsList from "./components/PostsList";
import AddPost from "./components/AddPost";
import "./App.css";
import useHttp from "./hooks/use-http";

function App() {
  const [posts, setPosts] = useState([]);
  const [post, setPost] = useState(null);

  const [createPost, setPageCreate] = useState(false);

  useEffect(() => {

    if (createPost) {
      setPageCreate(true);
    }
  });

  const handleClick = () => setPageCreate(true);

  const transformPosts = (postsObj) => {
    console.log('postsObj', postsObj.content);
    const loadedPosts = [];

    for (const key in postsObj.content) {
      loadedPosts.push({
        id: key,
        title: postsObj.content[key].title,
        description: postsObj.content[key].description,
        content: postsObj.content[key].content,

      });
    }

    setPosts(loadedPosts);
  };
  const { isLoading, error, fetchPostsHandler: fetchPosts } = useHttp(
    {
      url: "http://localhost:8080/api/v1/posts",
      method: "GET"
    },
    transformPosts
  );

  useEffect(() => {
    fetchPosts();
  }, []);

  const { fetchPostsHandler: addPostHandler } = useHttp({
    url: "http://localhost:8080/api/v1/posts",
    method: "POST",
    body: post
  });

  // async function addPostHandler(post) {
  //   const response = await fetch(
  //     "https://react-http-5af7d-default-rtdb.firebaseio.com/posts.json",
  //     {
  //       method: "POST",
  //       body: JSON.stringify(post),
  //       headers: {
  //         "Content-Type": "application/json"
  //       }
  //     }
  //   );
  //   const data = await response.json();
  //   console.log(data);
  // }

  let content = <p>Found no posts.</p>;

  if (posts.length > 0) {
    content = <PostsList posts={posts} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  const handleAddPost = (post) => {
    setPost(post);
    addPostHandler();
  };

  return (
    <React.Fragment>
      <section>
        <AddPost onAddPost={handleAddPost} />
      </section>
      <section>
        <button onClick={fetchPosts}>Fetch Posts</button>
      </section>
      <section>{content}</section>
      <section>
        <button onClick={createPost ? <Create/> : <Home handleClick={handleClick} />}>Create Post</button>
      </section>
    </React.Fragment>
  );
}

export default App;
