import React, { useState, useEffect, useCallback } from "react";

import PostsList from "./components/PostsList";
import AddPost from "./components/AddPost";
import "./App.css";
import useHttp from "./hooks/use-http";
import CreatePost from "./components/CreatePost";

function App() {
  const [posts, setPosts] = useState([]);
  const [post, setPost] = useState(null);

  const [createPost, setPageCreate] = useState(false);

  const handleChangePage = (page) => {
    setPageCreate(page)
  };

  const transformPosts = (postsObj) => {

    const loadedPosts = [];

    for (const key in postsObj.content) {
      loadedPosts.push({
        id: key,
        title: postsObj.content[key].title,
        description: postsObj.content[key].description,
        content: postsObj.content[key].content,

      });
    }

    setPosts(loadedPosts);
  };
  const { isLoading, error, fetchPostsHandler: fetchPosts } = useHttp(
    {
      url: "http://localhost:8080/api/v1/posts",
      method: "GET"
    },
    transformPosts
  );

  useEffect(() => {
    fetchPosts();
  }, []);

  const { fetchPostsHandler: addPostHandler } = useHttp({
    url: "http://localhost:8080/api/v1/posts",
    method: "POST",
    body: post
  });

  let content = <p>Found no posts.</p>;

  if (posts.length > 0) {
    content = <PostsList posts={posts} onChangePage={handleChangePage} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  const handleAddPost = async (data) => {
    await setPost(data);
    console.log('create post', post, data);
    addPostHandler();
  };

  const a = () => {
    setPost(data)
  }

  return (
    <React.Fragment>
      {/* <section>
        <AddPost onAddPost={handleAddPost} />
      </section> */}
      <section>
        <button onClick={fetchPosts} onChangePage={handleChangePage}>Home</button>
      </section>
      <section>
        {createPost ? <CreatePost onChangePage={handleChangePage} onAddPost={handleAddPost} /> : content}
      </section>
    </React.Fragment>
  );
}

export default App;
