import React, { useState } from 'react';

import Post from './Post';
import './PostsList.module.css';
import UpdatelPost from './UpdatelPost';

const PostList = (props) => {

  return (
    <div>

      <div className="container">
        <div className="row">
          <table className="table table-bordered">
            <thead className="thead-light">
              <tr>
                <th scope="col">STT</th>
                <th scope="col">Title</th>
                <th scope="col">Description</th>
                <th scope="col">Content</th>
                <th scope="col">Actions</th>
              </tr>
            </thead>
            <tbody>
              {props.posts && props.posts.map((post, index) =>
                <tr key={post.id}>
                  <td>{index + 1}</td>
                  <td>{post.title}</td>
                  <td>{post.description}</td>
                  <td>{post.content}</td>
                  <td>
                    <div className="d-flex justify-content-between align-items-center">
                      <div className="btn-group" style={{ marginBottom: "20px" }}>
                        <UpdatelPost to={`detail/${post.id}`} className="btn btn-sm btn-outline-secondary"/>
                        <button className="btn btn-sm btn-outline-secondary" onClick={() => props.deletePost(post.id)}>Delete</button>
                      </div>
                    </div>
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
    // <ul className={classes['posts-list']}>
    //   {props.posts.map((post) => (
    //     <Post
    //       key={post.id}
    //       title={post.title}
    //       releaseDate={post.releaseDate}
    //       openingText={post.openingText}
    //     />
    //   ))}
    // </ul>
  );
};

export default PostList;
