import React from 'react';

import Post from './Post';
import  './PostsList.module.css';

const PostList = (props) => {
  return (
    <div>
                {posts.length === 0 && (
                    <div className="text-center">
                        <h2>No post found at the moment</h2>
                    </div>
                )}
                <div className="container">
                    <div className="row">
                        <table className="table table-bordered">
                            <thead className="thead-light">
                                <tr>
                                    <th scope="col">STT</th>
                                    <th scope="col">Title</th>
                                    <th scope="col">Description</th>
                                    <th scope="col">Content</th>
                                    <th scope="col">CategoryId</th>
                                    <th scope="col">Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {props.posts && props.posts.map(post =>
                                    <tr key={post.id}>
                                        <td>{post.first_name}</td>
                                        <td>{post.last_name}</td>
                                        <td>{post.email}</td>
                                        <td>{post.phone}</td>
                                        <td>{post.address}</td>
                                        <td>{post.description}</td>
                                        <td>
                                            <div className="d-flex justify-content-between align-items-center">
                                                <div className="btn-group" style={{ marginBottom: "20px" }}>
                                                    <Link to={`edit/${post.id}`} className="btn btn-sm btn-outline-secondary">Edit Post </Link>
                                                    <button className="btn btn-sm btn-outline-secondary" onClick={() => this.deletePost(post.id)}>Delete Post</button>
                                                </div>
                                            </div>
                                        </td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
    // <ul className={classes['posts-list']}>
    //   {props.posts.map((post) => (
    //     <Post
    //       key={post.id}
    //       title={post.title}
    //       releaseDate={post.releaseDate}
    //       openingText={post.openingText}
    //     />
    //   ))}
    // </ul>
  );
};

export default PostList;
