import React, { useState, useEffect, useCallback } from "react";

import PostsList from "./components/PostsList";
import AddPost from "./components/AddPost";
import "./App.css";
import useHttp from "./hooks/use-http";

function App() {
  const [posts, setPosts] = useState([]);
  const [post, setPost] = useState(null);

  const transformPosts = (postsObj) => {
    const loadedPosts = [];

    for (const key in postsObj) {
      loadedPosts.push({
        id: key,
        title: postsObj[key].title,
        openingText: postsObj[key].openingText,
        releaseDate: postsObj[key].releaseDate
      });
    }

    setPosts(loadedPosts);
  };
  const { isLoading, error, fetchPostsHandler: fetchPosts } = useHttp(
    {
      url: "https://react-http-5af7d-default-rtdb.firebaseio.com/posts.json",
      method: "GET"
    },
    transformPosts
  );

  useEffect(() => {
    fetchPosts();
  }, []);

  const { fetchPostsHandler: addPostHandler } = useHttp({
    url: "https://react-http-5af7d-default-rtdb.firebaseio.com/posts.json",
    method: "POST",
    body: movie
  });

  // async function addMovieHandler(movie) {
  //   const response = await fetch(
  //     "https://react-http-5af7d-default-rtdb.firebaseio.com/posts.json",
  //     {
  //       method: "POST",
  //       body: JSON.stringify(movie),
  //       headers: {
  //         "Content-Type": "application/json"
  //       }
  //     }
  //   );
  //   const data = await response.json();
  //   console.log(data);
  // }

  let content = <p>Found no posts.</p>;

  if (posts.length > 0) {
    content = <PostsList posts={posts} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  const handleAddMovie = (movie) => {
    setMovie(movie);
    addMovieHandler();
  };

  return (
    <React.Fragment>
      <section>
        <AddMovie onAddMovie={handleAddMovie} />
      </section>
      <section>
        <button onClick={fetchPosts}>Fetch Posts</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
