import { useState } from "react";
import PostsList from "../components/PostsList";

const useHttp = (requestConfig, applyData) => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchPostsHandler = async (url, options) => {
    setIsLoading(true);
    setError(null);
    console.log("requestConfig.url",requestConfig.url);
    const requestUrl = url ?? requestConfig.url;
    const requestOptios = {
        method: requestConfig.method,
        headers: requestConfig.headers ? requestConfig.headers : {},
        body: options.body ? JSON.stringify(options.body) : requestConfig.body ? JSON.stringify(requestConfig.body) : null
    }
    try {
      const response = await fetch(requestUrl, requestOptios);
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      console.log('data',data);

      applyData(data);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  };

  return {
    isLoading,
    error,
    fetchPostsHandler
  };
};

export default useHttp;
