import { useState } from "react";
import useInput from "../hooks/use-input";

const isNotEmpty = (value) => value.trim() !== "";
const isDescription = (value) => value.length < 100;

const UpdatePost = (props) => {

    const submitHandler = (event) => {
        event.preventDefault();

        if (!formIsValid) {
            return;
        }

        const post = {
            id:props.post.id,
            content: props.post.content,
            description: props.post.description,
            title: props.post.title,
            categoryId:2
        };

        props.onUpdatePost(post);

        resetTitle();
        resetContent();
        resetDescription();
    };

    return (
        <form onSubmit={submitHandler}>
            <div className="control-group">
                <div className="">
                    <label htmlFor="name">Title</label>
                    <input
                        type="text"
                        id="name"
                        value={titleValue}
                        onChange={titleChangeHandler}
                        onBlur={titleBlurHandler}
                    />
                    {titleHasError && (
                        <p className="error-text">Please enter a title.</p>
                    )}
                </div>
                <div className={contentClasses}>
                    <label htmlFor="name">Content</label>
                    <input
                        type="text"
                        id="name"
                        value={props.post == null ? contentValue : props.post.content}
                        onChange={contentChangeHandler}
                        onBlur={contentBlurHandler}
                    />
                    {contentHasError && (
                        <p className="error-text">Please enter a content.</p>
                    )}
                </div>
            </div>
            <div className={descriptionClasses}>
                <label htmlFor="name">Description</label>
                <input
                    type="text"
                    id="name"
                    value={props.post == null ? descriptionValue : props.post.description}
                    onChange={descriptionChangeHandler}
                    onBlur={descriptionBlurHandler}
                />
                {descriptionHasError && (
                    <p className="error-text">Please enter a valid description.</p>
                )}
            </div>
            <div className="form-actions">
                <button disabled={!formIsValid}>Submit</button>
                <button className="button-cancel" onClick={() => {
                    props.onChangePage(false);
                }}>Cancel</button>
            </div>
        </form>
    );
};

export default UpdatePost;
